import "@fontsource/roboto";
import "@fontsource/arimo";
import styled from "styled-components"

export const UserItemsContainer = styled.div` 
    color: #FFFFFF;
    font-family: "Arimo", sans-serif;

    width: 100%;
    height: 75px;
    

    padding: 10px 10px;
    display: flex;
    justify-content: space-between;
    align-items: center;

    }


`

export const ButtonsContainer = styled.div`
    background: #808080;
    width: 100px;
    height: 100%;
    border: none;

    display: flex;
    justify-content: end;
    align-items: end;
    flex-direction: column;

    .removeButton{
        background: #FF000050;
        height: 25px;
        width: 75px;
        border-radius: 10px;
    
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }

    }

    .editButton{
        background: #FFFF0050;
        height: 25px;
        width: 75px;
        border-radius: 10px;
        
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }
    }
`

export const UserCredentialsContainer = styled.div`
    width: 100%;
    height: 100%;
`
