import { useForm } from "react-hook-form";
import { AddUserContainer, ButtonContainer } from "./styles";
import api from "../../services/api"

const AddUser = () => {

    const { register, handleSubmit } = useForm();

    const onSubmit = async (formData) => {
        api.post('/users', {
            name: `${formData.firstName}`,
            surname: `${formData.surname}`,
            role: `${formData.role}`,
            dateOfBirth: `${formData.dateOfBirth}`
        }, {
            headers: {
                'Content-Type': 'application/json'
            }     
        })

        console.log(formData);
    };

    const handleDelete = () => {
        
    }


    console.log("teste");
    return(
        <AddUserContainer>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register("firstName") } type="text" placeholder="Name"/>
                <input {...register("surname") } type="text" placeholder="Surname"/>
                <input {...register("role") } type="text" placeholder="Role"/>
                <input {...register("dateOfBirth") } type="date" placeholder="Date of Birth"/>
            
                <ButtonContainer>
                    <button type="submit">Add</button>
                    
                </ButtonContainer>
            </form>
        </AddUserContainer>
    )
}

export default AddUser;
