import styled from "styled-components";
import "@fontsource/roboto";
import "@fontsource/arimo";

export const AddUserContainer = styled.div`

    width: 100%;
    height: 108px;
    border-bottom: solid 1px #FF000050;

    padding: 0px 10px;
    display: flex;
    justify-content: space-between;

    form{
        width: 100%;
        display: flex;
        gap: 25px;
        align-items: center;
        

        input{
            background: #000000;
            height: 50px;
            width: 150px;
            border-radius: 10px;

            padding: 10px 10px;
            font-size: 16px;


            &::placeholder{
                font-family: "Arimo", sans-serif;
            }
        }

        button{
            margin-left: auto;
        }
    }


`

export const ButtonContainer = styled.div`
    background: #808080;
    width: 100px;
    height: 100%;
    border: none;

    display: flex;
    justify-content: end;
    align-items: center;

    .addButton{
        background: #39e75f50;
        height: 25px;
        width: 75px;
        border-radius: 10px;
        
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }

    }
      
    }
`
