import { useForm } from "react-hook-form";
import { AddUserContainer, ButtonContainer } from "./styles";

const AddUser = () => {

    const { register, handleSubmit } = useForm();

    const onSubmit = async (formData) => {
        try{
            const {data} = await api.get(`/users?login=${formData.login}&password=${formData.password}`);
            
            if(data.length && data[0].id){
                navigate('/') 
                return
            }

            alert('Usuário ou senha inválido')
        }catch(e){
            console.log(e)
        }
    };

    console.log("teste");
    return(
        <AddUserContainer>
            <form onSubmit={handleSubmit((data) => {
                console.log(data);
            })}>
                <input {...register("firstName")} type="text" placeholder="Name"/>
                <input {...register("surname")} type="text" placeholder="Surname"/>
                <input {...register("role")} type="text" placeholder="Role"/>
                <input {...register("dateOfBirth")} type="date" placeholder="Date of Birth"/>
            </form>
            <ButtonContainer>
                <input type="submit"/>
            </ButtonContainer>
        </AddUserContainer>
    )
}

export default AddUser;
