import "@fontsource/roboto";
import "@fontsource/arimo";
import styled from "styled-components"

export const UserItemsContainer = styled.div` 
    width: 100%;
    height: 50px;
    border-bottom: solid 1px #000000;

    padding: 0px 20px;
    display: flex;
    justify-content: space-between;

    form{
        width: 75%;
        display: flex;
        justify-content: space-between;
        align-items: center;

        input{
            height: 20px;
            width: 150px;
            border-radius: 10px;

            padding: 10px 10px;
            font-size: 16px;


            &::placeholder{
                font-family: "Arimo", sans-serif;
            }
        }
    }


`

export const ButtonsContainer = styled.div`
    background: #FFFFFF;
    width: 200px;
    height: 100%;
    border: none;

    display: flex;
    justify-content: end;
    align-items: center;

    .removeButton{
        background: #FF0000;
        height: 25px;
        width: 75px;
        border-radius: 10px;
        
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }

    }
`
