import "@fontsource/roboto";
import "@fontsource/arimo";
import styled from "styled-components"

export const UserItemsContainer = styled.div` 
    color: black;
    font-family: "Arimo", sans-serif;

    width: 100%;
    height: 75px;
    

    padding: 0px 10px;
    display: flex;
    align-items: center;
    gap: 195px;

    }


`

export const ButtonsContainer = styled.div`
    background: #808080;
    width: 75px;
    height: 100%;
    border: none;

    display: flex;
    justify-content: end;
    align-items: center;

    .removeButton{
        background: #FF000050;
        height: 25px;
        width: 75px;
        border-radius: 10px;
    
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }

    }

    .editButton{
        background: #FFFF0050;
        height: 25px;
        width: 75px;
        border-radius: 10px;
        
        font-family: "Arimo", sans-serif;

        &:hover{
            cursor: pointer;
        }
    }
`


export const ItemsPositionContainer = styled.div`
    height: 16px;
    min-width: 0px;
`
