          <EditUser/>
          <ExampleItem/>
          {users.map(user => <UserItems key={user.id} user={user} handleDelete={() => deleteUser(user.id)} handleUpdate={() => updateUser(user.id, user)}/> )}
        </Container>
      </div>
    </>

  );
}

export default App;
